import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.scss'],
})
export class FooterComponent {
  inputText = {
    email: '',
  };
  onSubscribe() {
    console.log(this.inputText.email);
  }
}
