import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { User } from '../user';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class SubscribeService {
  private apiKey = '306844aa7e99aee72976a91f2690ee80-us14'; //the book lovers

  constructor(private http: HttpClient) {}

  addEmail(param: string): Observable<any> {
    const baseUrl =
      'https://us14.api.mailchimp.com/3.0/lists/0f33d81abb/members';

    let header = new HttpHeaders();
    header = header.append('Content-Type', 'application/json');
    header = header.append('Authorization', 'Bearer' + this.apiKey);
    header = header.append('Access-Control-Allow-Origin', '*');

    this.http.post(baseUrl, param, { headers: header });
  }
}
